import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from "../firebase"
import { RecaptchaVerifier ,signInWithPhoneNumber} from 'firebase/auth';
import axios from 'axios';

import Login from "./Login"
import { logout,selectUser } from '../features/userSlice';
import { useDispatch,useSelector } from 'react-redux';
import userData from "../UserData.json"
import tick from "../assets/tick.png"


const Mainpage = () => {
    const [mobile, setMobile] = useState("")
    const navigate= useNavigate();
    const name=JSON.parse(localStorage.getItem("user")).name
    const email= JSON.parse(localStorage.getItem("user")).email
    
    
    const dispatch=useDispatch();
    const signOut =()=>
    {
        auth.signOut();
        localStorage.removeItem("user");
        dispatch(logout())
        navigate("/")
    }
    const handleNumber =(e)=>{
  
        return (
            setMobile(e.target.value)
    
    )
        }
    const sendOtp=()=>{
    
           let recaptcha =new RecaptchaVerifier("recaptcha");
           let number="+919894623878";
           auth.signInWithPhoneNumber(number,recaptcha).then(function(e){
            let code=prompt("enter the otp",'');
            if(code==null)return;
            e.confirm(code).then(function(result){
                console.log(result.user,'user');
                document.querySelector('label').textContent= result.user.phoneNumber+ "Number Verified";
            }).catch((error)=>{
                console.log(error)
            })
           })
        

    }

    return(
        <div>
            
            
            Welcome {name}!
            {userData.map((data => {
                if (data.Email === email )
                {

                if(data.IsMobileVerified)
                {
                    return(
                        <div>
                        <div>Name:{data.Name}</div>
                        <div>Email:{data.Email}</div>
                        <div>Mobile:{data.Mobile}<img src={tick}></img></div>
                        
                    
                        
                        </div>
                    )
                    
                }
                else{
                    return(
                <div>
                <div>Name:{data.Name}</div>
                <div>Email:{data.Email}</div>
                <div>Enter your mobile number: <input type="text" id='phoneNumber' onChange={handleNumber}></input></div>
                <div><button id='sendCode' onClick={sendOtp}>Send OTP</button></div>
                <label></label>
                
            
                
                </div>
                    );
            }
        }
        
    }))}
            <button style={{"marginLeft" : "20px"}} 
            onClick={signOut} >
                Logout
            </button>
            
            
        </div>
);
}
  
export default Mainpage;
