import React from 'react';
import { useEffect } from 'react';
import { firebase } from '../firebase';
import { RecaptchaVerifier,getAuth } from 'firebase/auth';


const Otp = ({ loginSubmit, otpSubmit, viewOtpForm}) => {
    const otpauth= getAuth();
    useEffect(() => {
        window.recaptchaVerifier = new firebase.otpauth.RecaptchaVerifier(
            "recaptcha-container", {
                size: "invisible",
                callback: function(response) {
                    console.log("Captcha Resolved");
                },
                defaultCountry: "IN",
            }
        );
    }, []);
    return (<div>
         {!viewOtpForm ? (
        <div className="form-wrapper">
          <form id="loginForm" onSubmit={loginSubmit}>
            <div className="input-field">
              <label>Phone Number</label>
              <input
                type="text"
                placeholder="Phone"
                name="phone"
                autoComplete="false"
              />
            </div>
            <button className="main-button" type="submit" id="sign-in-button">
              Sign in
            </button>
          </form>
        </div>
      ) : (
        <div className="form-wrapper" onSubmit={otpSubmit}>
          <form id="otpForm">
            <div className="input-field">
              <label>Enter OTP</label>
              <input
                type="number"
                placeholder="One time password"
                name="otp_value"
                autoComplete="false"
              />
            </div>
            <button className="main-button" type="submit">
              Verify OTP
            </button>
          </form>
        </div>
      )}
    </div>);
}


export default Otp;
