import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from "../firebase"
import { RecaptchaVerifier ,signInWithPhoneNumber} from 'firebase/auth';
import axios from 'axios';

import Login from "./Login"
import { logout,selectUser } from '../features/userSlice';
import { useDispatch,useSelector } from 'react-redux';
import userData from "../UserData.json"
import tick from "../assets/tick.png"
import Otp from './Otp';


const Mainpage = () => {
    const [mobile, setMobile] = useState("")
    const navigate= useNavigate();
    const name=JSON.parse(localStorage.getItem("user")).name
    const email= JSON.parse(localStorage.getItem("user")).email
    
    
    const dispatch=useDispatch();
    
    const signOut =()=>
    {
        auth.signOut();
        localStorage.removeItem("user");
        dispatch(logout())
        navigate("/")
    }
    const handleNumber =(e)=>{
  
        return (
            setMobile(e.target.value)
    
    )
        }
    const sendOtp=()=>{
    
         
        

    }

    return(
        <div>
            
            
            Welcome {name}!
            {userData.map((data => {
                if (data.Email === email )
                {

                if(data.IsMobileVerified)
                {
                    return(
                        <div>
                        <div>Name:{data.Name}</div>
                        <div>Email:{data.Email}</div>
                        <div>Mobile:{data.Mobile}<img src={tick}></img></div>
                        
                    
                        
                        </div>
                    )
                    
                }
                else{
                    return(
                <div>
                <div>Name:{data.Name}</div>
                <div>Email:{data.Email}</div>
                <div>Enter your mobile number: <input type="text" id='phoneNumber' onChange={handleNumber}></input></div>
                <p className="sub-text">Verify your mobile number.</p>
                <Otp />
                <label></label>
                
            
                
                </div>
                    );
            }
        }
        
    }))}
            <button style={{"marginLeft" : "20px"}} 
            onClick={signOut} >
                Logout
            </button>
            
            
        </div>
);
}
  
export default Mainpage;
