import React,{useState,useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from "../firebase"
import { getAuth, RecaptchaVerifier ,signInWithPhoneNumber} from 'firebase/auth';
import axios from 'axios';
import { firebase } from 'firebase/app';
import Login from "./Login"
import { logout,selectUser } from '../features/userSlice';
import { useDispatch,useSelector } from 'react-redux';
import userData from "../UserData.json"
import tick from "../assets/tick.png"
import Otp from './Otp';
import {writeJsonFile} from 'write-json-file';


const Mainpage = () => {
    const [mobile, setMobile] = useState("")
    const userArray=userData;
    const otpauth= getAuth();
    const [verify,setVerify]=useState(false)
    
    const [viewOtpForm, setViewOtpForm] = useState(false);
    const navigate= useNavigate();
    const name=JSON.parse(localStorage.getItem("user")).name
    const email= JSON.parse(localStorage.getItem("user")).email
    
    
    const dispatch=useDispatch();
    
       
    const signOut =()=>
    {
        auth.signOut();
        localStorage.removeItem("user");
        dispatch(logout())
        navigate("/")
    }
    const handleNumber =(e)=>{
  
        return (
            setMobile(e.target.value)
    
    )
        }
   
    const loginSubmit = (e) => {
        e.preventDefault();

        window.recaptchaVerifier = new RecaptchaVerifier('recaptcha-container', {
            "size":"invisible",
            'callback': (response) => {
                console.log(response)
                // ...
              },
        }, otpauth);
    
        let phone_number = e.target.phone.value;
        const appVerifier = window.recaptchaVerifier;
    
        signInWithPhoneNumber(otpauth,phone_number, appVerifier)
            .then((confirmationResult) => {
                // SMS sent. Prompt user to type the code from the message, then sign the
                // user in with confirmationResult.confirm(code).
                console.log("otp sent");
                setViewOtpForm(true);
                window.confirmationResult = confirmationResult;
                // ...
            })
            .catch((error) => {
                // Error; SMS not sent
                // ...
                alert(error.message);
            });
    };
    const otpSubmit = (e) => {
        e.preventDefault();
    
        let opt_number = e.target.otp_value.value;
    
        window.confirmationResult
            .confirm(opt_number)
            .then((confirmationResult) => {
                console.log(confirmationResult);
                console.log("success");
                setVerify(true)
                userArray.push(data.IsMobileVerified=true)
                writeData()

            
            })
            .catch((error) => {
                // User couldn't sign in (bad verification code?)
                alert(error.message);
            });
    };
    async function writeData(){
        await writeJsonFile('userData.json', userArray);
    }
    return(
        <div>
            
            
            Welcome {name}!
            {console.log(userArray)}

            {userArray.map((data => {

                if (data.Email === email )
                {

                if(data.IsMobileVerified)
                {
                    return(
                        <div>
                        <div>Name:{data.Name}</div>
                        <div>Email:{data.Email}</div>
                        <div>Mobile:{data.Mobile}<img src={tick}></img></div>
                        
                    
                        
                        </div>
                    )
                    
                }
                else{
                    return(
                <div>
                <div>Name:{data.Name}</div>
                <div>Email:{data.Email}</div>
                
                <p className="sub-text">Verify your mobile number.</p>
                <label id="recaptcha-container"></label>
                {verify?
                (userArray.push(data.IsMobileVerified=true))
                
                
                
                
                
                :
                <Otp loginSubmit={loginSubmit} otpSubmit={otpSubmit} viewOtpForm={viewOtpForm}/>
                
                   
                
                
                    }
            
                
                </div>
                    );
                    {console.log(userArray)}
            }
        }
        
    }))}
            <button style={{"marginLeft" : "20px"}} 
            onClick={signOut} >
                Logout
            </button>
            
            
        </div>
);
}
  
export default Mainpage;
