import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';

import {auth,provider,fprovider} from "../firebase"
import { getAuth, signInWithPopup, GoogleAuthProvider ,FacebookAuthProvider} from "firebase/auth";
import { login } from '../features/userSlice';
import { useDispatch } from 'react-redux';


  
const Login = () => {
    const navigate= useNavigate();
    const dispatch = useDispatch();
  
 const signinGoogle=()=>{
    signInWithPopup(auth, provider)
            .then((result) => {
                // This gives you a Google Access Token. You can use it to access the Google API.
                const credential = GoogleAuthProvider.credentialFromResult(result);
                const token = credential.accessToken;
                // The signed-in user info.
                const user = result.user;
                localStorage.setItem("user", JSON.stringify({name:user.displayName,
                email:user.email,
                uid:user.uid}))
                dispatch(login(localStorage.getItem("user")));

                navigate("/profile")

               
                // ...
            }).catch((error) => {
                // Handle Errors here.
                const errorCode = error.code;
                const errorMessage = error.message;
                // The email of the user's account used.
                const email = error.customData.email;
                // The AuthCredential type that was used.
                const credential = GoogleAuthProvider.credentialFromError(error);
                // ...
            });
 }
    

    
    





      
    return (
        <div>
    
                <center>
                <button style={{"marginTop" : "200px"}} onClick={signinGoogle}>Sign In with Google</button>
                </center>
                
            
        </div>
    );
}
  
export default Login;
