import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from "../firebase"
import { RecaptchaVerifier ,signInWithPhoneNumber} from 'firebase/auth';
import axios from 'axios';

import Login from "./Login"
import { logout,selectUser } from '../features/userSlice';
import { useDispatch,useSelector } from 'react-redux';
import userData from "../UserData.json"
import tick from "../assets/tick.png"


const Mainpage = ({ loginSubmit, otpSubmit, viewOtpForm}) => {
    const [mobile, setMobile] = useState("")
    const navigate= useNavigate();
    const name=JSON.parse(localStorage.getItem("user")).name
    const email= JSON.parse(localStorage.getItem("user")).email
    
    
    const dispatch=useDispatch();
    useEffect(() => {
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier(
            "recaptcha-container", {
                size: "invisible",
                callback: function(response) {
                    console.log("Captcha Resolved");
                },
                defaultCountry: "IN",
            }
        );
    }, []);
    const signOut =()=>
    {
        auth.signOut();
        localStorage.removeItem("user");
        dispatch(logout())
        navigate("/")
    }
    const handleNumber =(e)=>{
  
        return (
            setMobile(e.target.value)
    
    )
        }
    const sendOtp=()=>{
    
         
        

    }

    return(
        <div>
            
            
            Welcome {name}!
            {userData.map((data => {
                if (data.Email === email )
                {

                if(data.IsMobileVerified)
                {
                    return(
                        <div>
                        <div>Name:{data.Name}</div>
                        <div>Email:{data.Email}</div>
                        <div>Mobile:{data.Mobile}<img src={tick}></img></div>
                        
                    
                        
                        </div>
                    )
                    
                }
                else{
                    return(
                <div>
                <div>Name:{data.Name}</div>
                <div>Email:{data.Email}</div>
                <div>Enter your mobile number: <input type="text" id='phoneNumber' onChange={handleNumber}></input></div>
                <p className="sub-text">Verify your mobile number.</p>
      {!viewOtpForm ? (
        <div className="form-wrapper">
          <form id="loginForm" onSubmit={loginSubmit}>
            <div className="input-field">
              <label>Phone Number</label>
              <input
                type="text"
                placeholder="Phone"
                name="phone"
                autoComplete="false"
              />
            </div>
            <button className="main-button" type="submit" id="sign-in-button">
              Sign in
            </button>
          </form>
        </div>
      ) : (
        <div className="form-wrapper" onSubmit={otpSubmit}>
          <form id="otpForm">
            <div className="input-field">
              <label>Enter OTP</label>
              <input
                type="number"
                placeholder="One time password"
                name="otp_value"
                autoComplete="false"
              />
            </div>
            <button className="main-button" type="submit">
              Verify OTP
            </button>
          </form>
        </div>
      )}
                <label></label>
                
            
                
                </div>
                    );
            }
        }
        
    }))}
            <button style={{"marginLeft" : "20px"}} 
            onClick={signOut} >
                Logout
            </button>
            
            
        </div>
);
}
  
export default Mainpage;
