import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from "../firebase"
import { RecaptchaVerifier } from 'firebase/auth';
import axios from 'axios';

import Login from "./Login"
import { logout,selectUser } from '../features/userSlice';
import { useDispatch,useSelector } from 'react-redux';
import userData from "../UserData.json"
import tick from "../assets/tick.png"


const Mainpage = () => {
    const [mobile, setMobile] = useState("")
    const navigate= useNavigate();
    const name=JSON.parse(localStorage.getItem("user")).name
    const email= JSON.parse(localStorage.getItem("user")).email
    
    
    const dispatch=useDispatch();
    const signOut =()=>
    {
        auth.signOut();
        localStorage.removeItem("user");
        dispatch(logout())
        navigate("/")
    }
    const handleNumber =(e)=>{
  
        return (
            setMobile(e.target.value)
    
    )
        }
    const sendOtp=()=>{
        
            window.recaptchaVerifier = new RecaptchaVerifier('sendCode', {
                'size': 'invisible',
                'callback': function (recapchaToken) {
                  // reCAPTCHA solved, send recapchaToken and phone number to backend.
                  
                  // a REST call to your backend
                  axios.post({
                    url: 'localhost:3000',
                    body: {
                      phoneNumber: document.getElementById('phoneNumber').value,
                      recapchaToken,
                    }
                  });
                }
              });
              
              // render the rapchaVerifier. 
              window.recaptchaVerifier.render().then(function (widgetId) {
                window.recaptchaWidgetId = widgetId;
              });
              const recaptchaResponse = grecaptcha.getResponse(recaptchaWidgetId);
              console.log(recaptchaResponse);
    

    }

    return(
        <div>
            
            
            Welcome {name}!
            {userData.map((data => {
                if (data.Email === email )
                {

                if(data.IsMobileVerified)
                {
                    return(
                        <div>
                        <div>Name:{data.Name}</div>
                        <div>Email:{data.Email}</div>
                        <div>Mobile:{data.Mobile}<img src={tick}></img></div>
                        
                    
                        
                        </div>
                    )
                    
                }
                else{
                    return(
                <div>
                <div>Name:{data.Name}</div>
                <div>Email:{data.Email}</div>
                <div>Enter your mobile number: <input type="text" id='phoneNumber' onChange={handleNumber}></input></div>
                <div><button id='sendCode' onClick={sendOtp}>Send OTP</button></div>
                
                
            
                
                </div>
                    );
            }
        }
        
    }))}
            <button style={{"marginLeft" : "20px"}} 
            onClick={signOut} >
                Logout
            </button>
            
            
        </div>
);
}
  
export default Mainpage;
